<div ng-controller="RestaurantController"><div><a href="#!/restaurantes" class="btn"><span class="fa fa-chevron-left"></span> Volver al listado</a></div><form id="restaurantEdit" ng-submit="save()"><div class="row"><div class="input-field"><input type="text" ng-model="restaurante.nombre" value="{{restaurante.nombre}}"/><label>Nombre</label></div><div class="input-field"><input type="tel" ng-model="restaurante.telefono" value="{{restaurante.telefono}}"/><label>Teléfono</label></div><div class="input-field"><textarea ng-model="restaurante.direccion" value="{{restaurante.direccion}}" class="materialize-textarea"></textarea><label>Dirección</label></div></div><div><div ng-repeat="dia in restaurante.horarios | orderBy: id " class="row"><div class="input-field"><input type="checkbox" value="{{$dia.id}}" ng-checked="dia.checked==true" ng-change="updateHorario()" ng-model="restaurante.horarios[dia.id]['checked']" id="{{dia.id+dia.nombre}}"/><label for="{{dia.id+dia.nombre}}">{{dia.nombre}}</label></div><div class="input-field"><div ng-show="dia.checked"><table class="bordered responsive-table"><tbody id="horarios_{{dia.id}}"><tr ng-repeat="horario in dia.horarios"><td><div class="input-field"><div class="p5"><input type="text" ng-timepicker="ng-timepicker" ng-model="dia.horarios[$index].apertura"/><label>Apertura</label></div></div></td><td><div class="input-field"><div class="p5"><input type="text" ng-timepicker="ng-timepicker" ng-model="dia.horarios[$index].cierre"/><label>Cierre</label></div></div></td><td><div class="input-field"><div class="p5"><a href="javascript:void(0)" ng-click="removeHorario(dia.id,$index)">Eliminar</a></div></div></td></tr></tbody></table><button ng-click="addHorario($index)" type="button" class="btn light-blue darken-4 mt20">Agregar <span class="fa fa-plus"></span></button></div></div></div></div><div class="mt20"><button type="submit" class="btn">Guardar <span class="fa fa-hdd-o"></span></button></div></form></div>